import { firestoreReadText } from "./FirebaseAPI";
const TaskList = () => {
    const rows = [];
    for (let i = 0; i < numRows; i++) {
        firestoreReadText((id, text, err) => {
            if (err != null) {
                rows.push(<TaskRow displayText={text} id={id}/>);
            } else {
                return err
            }
        })
    }
    return (
        <div>
            {rows}
        </div>
    );
}

export default TaskList;
